    /**
     * @throws \DCorePHP\Exception\InvalidApiCallException
     * @throws \DCorePHP\Exception\ValidationException
     * @throws \DCorePHP\Model\InvalidOperationTypeException
     * @throws \WebSocket\BadOpcodeException
     */
    public function testGetOperation(): void
    {
        if ($this->websocketMock) {
            $this->websocketMock
                ->expects($this->exactly(3))
                ->method('send')
                ->withConsecutive(
                    [$this->callback(function(BaseRequest $req) { return $req->setId(1)->toJson() === '{"jsonrpc":"2.0","id":1,"method":"call","params":[1,"login",["",""]]}'; })],
                    [$this->callback(function(BaseRequest $req) { return $req->setId(2)->toJson() === '{"jsonrpc":"2.0","id":2,"method":"call","params":[1,"history",["",""]]}'; })],
                    [$this->callback(function(BaseRequest $req) { return $req->setId(3)->toJson() === '{"jsonrpc":"2.0","id":3,"method":"call","params":[6,"get_account_balance_for_transaction",["1.2.27","1.7.919365"]]}'; })]
                )
                ->will($this->onConsecutiveCalls(
                    Login::responseToModel(new BaseResponse('{"id":1,"result":true}')),
                    History::responseToModel(new BaseResponse('{"id":2,"result":6}')),
                    GetAccountBalanceForTransaction::responseToModel(new BaseResponse('{"id":3,"result":{"hist_object":{"id":"1.7.919365","op":[42,{"fee":{"amount":0,"asset_id":"1.3.0"},"author":"1.2.27","escrow":{"amount":1000000,"asset_id":"1.3.0"},"content":"2.13.172"}],"result":[0,{}],"block_num":630110,"trx_in_block":0,"op_in_trx":1,"virtual_op":6012},"balance":{"asset0":{"amount":1000000,"asset_id":"1.3.0"},"asset1":{"amount":0,"asset_id":"1.3.0"}},"fee":{"amount":0,"asset_id":"1.3.0"}}}'))
                ));
        }

        $operation = $this->sdk->getHistoryApi()->getOperation(new ChainObject(DCoreSDKTest::ACCOUNT_ID_1), new ChainObject('1.7.919365'));
        $this->assertEquals('630110', $operation->getOperation()->getBlockNum());
        $this->assertEquals('6012', $operation->getOperation()->getVirtualOperation());
        $this->assertEquals(0, $operation->getFee()->getAmount());
    }

